function applyEffects(){const socialIcons=document.querySelectorAll('.share-buttons');const randomClasses=['fa-shake','fa-beat','fa-beat-fade','fa-bounce','fa-fade','fa-flip','fa-spin'];socialIcons.forEach(icon=>{icon.addEventListener('mouseenter',()=>{const randomIndex=Math.floor(Math.random()*randomClasses.length);icon.classList.add(randomClasses[randomIndex]);});icon.addEventListener('mouseleave',()=>{icon.classList.remove(...randomClasses);});});}