function updateFileNamePlaceholder(inputId,spanId){const fileInput=document.querySelector(`#${inputId}`);const fileNamePlaceholder=document.querySelector(`#${spanId}`);fileInput.addEventListener('change',function(){if(fileInput.files.length>0){fileNamePlaceholder.textContent=fileInput.files[0].name;}else{fileNamePlaceholder.textContent='Upload Payment Proof';}});}